mapper.select:
 SELECT D.DIVISION_CODE AS code,
		D.DIVISION_NAME AS name FROM KPC_DIVISION_INFO D WHERE  D.DIVISION_CODE=#{code} 

		D.DIVISION_CODE > 0  ORDER BY D.DIVISION_CODE


SELECT * FROM(
	<include refid="getProjectScore"/>
)
WHERE (	E.EMPLOYE_ID = #{employeId}
	OR	A.AUTHORITY_LEVEL < #{authCode}	)


		SELECT COUNT(*) AS cnt
		  FROM KPC_EMPLOYE E
          LEFT OUTER JOIN KPC_PERSONAL_PROJECT_SCORE PS
		    ON E.EMPLOYE_ID = PS.EMPLOYE_ID AND PS.MONTH = #{month}
		 WHERE E.EMPLOYE_ID = #{employeId}

mapper.delete:
 DELETE FROM
		KPC_PERSONAL_PERFORMANCE_SCORE WHERE EMPLOYE_ID = #{employeId} AND MONTH = #{month} AND
		CONFIRM_NUMBER = #{confirmNumber}

mapper.select:
SELECT * FROM ( SELECT VS.SEQ AS seq, VS.MONTH AS month, E.EMPLOYE_ID AS employeId, E.NAME AS employeName , D.DIVISION_NAME AS divisionName, E.DIVISION_CODE AS divisionCode, A.AUTHORITY_LEVEL AS authLevel, T.TEAM_NAME AS teamName, NVL(VS.SCORE1,0) AS score1, NVL(VS.SCORE2,0) AS score2, NVL(VS.SCORE3,0) AS score3, NVL(VS.SCORE4,0) AS score4, NVL(VS.SCORE5,0) AS score5, NVL(VS.SCORE6,0) AS score6, NVL(VS.SCORE7,0) AS score7, NVL(VS.SCORE8,0) AS score8, NVL(VS.STATUS,0) AS status, VS.COMMENTS AS comments, NVL(VS.CONFIRM_NUMBER,1) AS confirmNumber FROM KPC_EMPLOYE E, KPC_DIVISION_INFO D, KPC_TEAM_INFO T, KPC_PERSONAL_AUTHORITY A, KPC_PERSONAL_VALUE_SCORE VS WHERE E.EMPLOYE_ID=A.EMPLOYE_ID(+) AND E.TEAM_CODE = T.TEAM_CODE(+) AND E.DIVISION_CODE = D.DIVISION_CODE(+) AND E.EMPLOYE_ID = VS.EMPLOYE_ID(+) AND E.USE_YN='Y' AND A.AUTHORITY_LEVEL < 4 AND VS.CONFIRM_NUMBER(+) = 1 AND VS.MONTH(+) = #{month} UNION SELECT VS.SEQ AS seq, VS.MONTH AS month, E.EMPLOYE_ID AS employeId, E.NAME AS employeName , D.DIVISION_NAME AS divisionName, E.DIVISION_CODE AS divisionCode, A.AUTHORITY_LEVEL AS authLevel, T.TEAM_NAME AS teamName, NVL(VS.SCORE1,0) AS score1, NVL(VS.SCORE2,0) AS score2, NVL(VS.SCORE3,0) AS score3, NVL(VS.SCORE4,0) AS score4, NVL(VS.SCORE5,0) AS score5, NVL(VS.SCORE6,0) AS score6, NVL(VS.SCORE7,0) AS score7, NVL(VS.SCORE8,0) AS score8, NVL(VS.STATUS,0) AS status, VS.COMMENTS AS comments, NVL(VS.CONFIRM_NUMBER,2) AS confirmNumber FROM KPC_EMPLOYE E, KPC_DIVISION_INFO D, KPC_TEAM_INFO T, KPC_PERSONAL_AUTHORITY A, KPC_PERSONAL_VALUE_SCORE VS WHERE E.EMPLOYE_ID=A.EMPLOYE_ID(+) AND E.TEAM_CODE = T.TEAM_CODE(+) AND E.DIVISION_CODE = D.DIVISION_CODE(+) AND E.EMPLOYE_ID = VS.EMPLOYE_ID(+) AND E.USE_YN='Y' AND A.AUTHORITY_LEVEL < 4 AND VS.CONFIRM_NUMBER(+) = 2 AND VS.MONTH(+) = #{month} ) employeId = #{employeId}
divisionCode = ( SELECT DIVISION_CODE FROM KPC_EMPLOYE WHERE EMPLOYE_ID=#{employeId} )
ORDER BY employeName

 SELECT
		COUNT(*) cnt FROM KPC_PERSONAL_PERFORMANCE_SCORE WHERE MONTH = #{month} AND EMPLOYE_ID =
		#{employeId} AND CONFIRM_NUMBER = #{confirmNumber}

 SELECT COUNT(*)
		cnt FROM KPC_PERSONAL_VALUE_SCORE WHERE MONTH = #{month} AND EMPLOYE_ID = #{employeId} AND
		CONFIRM_NUMBER = #{confirmNumber}


SELECT * FROM(
	<include refid="getProjectScore"/>
)
WHERE (	E.EMPLOYE_ID = #{employeId}
	OR	A.AUTHORITY_LEVEL < #{authCode}	)

SELECT * FROM ( SELECT VS.SEQ AS seq, VS.MONTH AS month, E.EMPLOYE_ID AS employeId, E.NAME AS employeName , D.DIVISION_NAME AS divisionName, E.DIVISION_CODE AS divisionCode, A.AUTHORITY_LEVEL AS authLevel, T.TEAM_NAME AS teamName, NVL(VS.SCORE1,0) AS score1, NVL(VS.SCORE2,0) AS score2, NVL(VS.SCORE3,0) AS score3, NVL(VS.SCORE4,0) AS score4, NVL(VS.SCORE5,0) AS score5, NVL(VS.SCORE6,0) AS score6, NVL(VS.SCORE7,0) AS score7, NVL(VS.SCORE8,0) AS score8, NVL(VS.STATUS,0) AS status, VS.COMMENTS AS comments, NVL(VS.CONFIRM_NUMBER,1) AS confirmNumber FROM KPC_EMPLOYE E, KPC_DIVISION_INFO D, KPC_TEAM_INFO T, KPC_PERSONAL_AUTHORITY A, KPC_PERSONAL_VALUE_SCORE_HST VS WHERE E.EMPLOYE_ID=A.EMPLOYE_ID(+) AND E.TEAM_CODE = T.TEAM_CODE(+) AND E.DIVISION_CODE = D.DIVISION_CODE(+) AND E.EMPLOYE_ID = VS.EMPLOYE_ID(+) AND E.USE_YN='Y' AND A.AUTHORITY_LEVEL < 4 AND VS.CONFIRM_NUMBER(+) = 1 AND VS.MONTH(+) = #{month} UNION SELECT VS.SEQ AS seq, VS.MONTH AS month, E.EMPLOYE_ID AS employeId, E.NAME AS employeName , D.DIVISION_NAME AS divisionName, E.DIVISION_CODE AS divisionCode, A.AUTHORITY_LEVEL AS authLevel, T.TEAM_NAME AS teamName, NVL(VS.SCORE1,0) AS score1, NVL(VS.SCORE2,0) AS score2, NVL(VS.SCORE3,0) AS score3, NVL(VS.SCORE4,0) AS score4, NVL(VS.SCORE5,0) AS score5, NVL(VS.SCORE6,0) AS score6, NVL(VS.SCORE7,0) AS score7, NVL(VS.SCORE8,0) AS score8, NVL(VS.STATUS,0) AS status, VS.COMMENTS AS comments, NVL(VS.CONFIRM_NUMBER,2) AS confirmNumber FROM KPC_EMPLOYE E, KPC_DIVISION_INFO D, KPC_TEAM_INFO T, KPC_PERSONAL_AUTHORITY A, KPC_PERSONAL_VALUE_SCORE_HST VS WHERE E.EMPLOYE_ID=A.EMPLOYE_ID(+) AND E.TEAM_CODE = T.TEAM_CODE(+) AND E.DIVISION_CODE = D.DIVISION_CODE(+) AND E.EMPLOYE_ID = VS.EMPLOYE_ID(+) AND E.USE_YN='Y' AND A.AUTHORITY_LEVEL < 4 AND VS.CONFIRM_NUMBER(+) = 2 AND VS.MONTH(+) = #{month} ) employeId = #{employeId}
divisionCode = ( SELECT DIVISION_CODE FROM KPC_EMPLOYE WHERE EMPLOYE_ID=#{employeId} )
ORDER BY employeName
